<?phpclass block_straw_poll_edit_form extends block_edit_form {     protected function specific_definition($mform) {         // Section header title according to language file.        $mform->addElement('header', 'configheader', get_string('pluginname', 'block_straw_poll'));         // A sample string variable with a default value.        $mform->addElement('text', 'config_question', get_string('questiontext', 'block_straw_poll'),'size="60"');        $mform->setType('config_question', PARAM_MULTILANG);     		$mform->addRule('config_question', get_string('required'),'required',null,'client');		        $mform->addElement('text', 'config_choice1', get_string('choice1', 'block_straw_poll'),'size="50"');        $mform->setType('config_choice1', PARAM_MULTILANG);     		$mform->addRule('config_choice1', get_string('required'),'required',null,'client');	        $mform->addElement('text', 'config_choice2', get_string('choice2', 'block_straw_poll'),'size="50"');        $mform->setType('config_choice2', PARAM_MULTILANG);     		$mform->addRule('config_choice2', get_string('required'),'required',null,'client');				        $mform->addElement('text', 'config_choice3', get_string('choice3', 'block_straw_poll'),'size="50"');        $mform->setType('config_choice3', PARAM_MULTILANG);             $mform->addElement('text', 'config_choice4', get_string('choice4', 'block_straw_poll'),'size="50"');        $mform->setType('config_choice4', PARAM_MULTILANG);            $mform->addElement('text', 'config_choice5', get_string('choice5', 'block_straw_poll'),'size="50"');        $mform->setType('config_choice5', PARAM_MULTILANG);             $mform->addElement('text', 'config_choice6', get_string('choice6', 'block_straw_poll'),'size="50"');        $mform->setType('config_choice6', PARAM_MULTILANG);  						$mform->addElement('selectyesno', 'config_canchange', get_string('canchange', 'block_straw_poll'));						$seeresultoptions = array();		for ($i=0;$i<3;$i++){			$seeresultoptions[$i] = get_string('seeresults'.$i, 'block_straw_poll');		}		$mform->addElement('select', 'config_seeresults', get_string('seeresults', 'block_straw_poll'), $seeresultoptions);        $mform->setDefault('config_seeresults', 1);		$mform->addElement('date_time_selector', 'config_ends', get_string('pollends', 'block_straw_poll'));        $mform->setDefault('config_ends',  strtotime('friday next week 17:00'));		$mform->addRule('config_ends', get_string('required'),'required',null,'client');		    }}